package com.pack1;

public class Print_Statement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=100,y=500,z=300;
		System.out.println(x<y&&z<y);
		System.out.println(x==y&&z<y);
		System.out.println(x==y||x<y);
		System.out.println(x==y||x>y);
		System.out.println(!(x>y));
		System.out.println(!(x<y));

	}

}
